import React, { Component } from 'react';
import {
	Card, CardImg, CardText, CardBody, Breadcrumb, BreadcrumbItem, Button, Modal,
	ModalHeader, ModalBody, Form, FormGroup, Input, Label, Col, Row} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Control, LocalForm, Errors } from 'react-redux-form';

const required = (val) => val && val.length;
const maxLength = (len) => (val) => !val || val.length <= len;
const minLength = (len) => (val) => val && val.length >= len;
const isNumber = (val) => !isNaN(+val);
const validEmail = (val) => /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val);
function RenderCampsite({ campsite }) {
	return (
		<div className="col-md-5 m-1">
			<Card>
				<CardImg top src={campsite.image} alt={campsite.name} />
				<CardBody>
					<CardText>{campsite.description} </CardText>
				</CardBody>
			</Card>
		</div>
	);
}
const commentModal = (props) => {

}
class CommentForm extends Component {
	constructor(props) {
		super(props);

		this.state = {
			isModalOpen: false
		};
		this.toggleModal = this.toggleModal.bind(this);
	}

	toggleModal() {
		this.setState({
			isModalOpen: !this.state.isModalOpen
		});
	}

	render() {
		return (
			<React.Fragment>
				<Button outline onClick={this.toggleModal}>
					<i className="fa fa-pencil fa-lg">Submit Comment</i>
				</Button>
				<Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
					<ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
					<ModalBody>
						<LocalForm onSubmit={(values) => this.handleSubmit(values)}>

							<Row className="form-group">
								<Label htmlFor="Name" md={2}>
									 Name
								</Label>
								<Col md={10}>
									<Control.text
										model=".Name"
										id="Name"
										name="Name"
										placeholder=" Name"
										className="form-control"
										validators={{
											required,
											minLength: minLength(2),
											maxLength: maxLength(15)
										}}
									/>
									<Errors
										className="text-danger"
										model=".lastName"
										show="touched"
										component="div"
										messages={{
											required: 'Required',
											minLength: 'Must be at least 2 characters',
											maxLength: 'Must be 15 characters or less'
										}}
									/>
								</Col>
							</Row>
							<Row className="form-group">
								<Label htmlFor="rating" md={2}>
									Comments
								</Label>
								<Col md={10}>
									<Control.select model=".rating" name="contactType" className="form-control">
										<option>By Phone</option>
										<option>By Email</option>
									</Control.select>
								</Col>
							</Row>
							<Row className="form-group">
								<Label htmlFor="feedback" md={2}>
									Comments							
								</Label>
								<Col md={10}>
									<Control.textarea
										model=".feedback"
										id="feedback"
										name="feedback"
										rows="12"
										className="form-control"
									/>
								</Col>
							</Row>
							<Row className="form-group">
								<Col md={{ size: 10, offset: 2 }}>

									<Button type="submit" color="primary">
										Submit
									</Button>
								</Col>
							</Row>
						</LocalForm>
					</ModalBody>
				</Modal>
			</React.Fragment>
		);
	}
}
function RenderComments({ comments }) {
	console.log('comments are', comments);
	if (comments) {
		return (
			<div className="col-md-5 m-1">
				<h4>Comments</h4>
				{comments.map((comment) => {
					return (
						<div key={comment.id}>
							<p>
								{comment.text}
								<br />
								-- {comment.author},{' '}
								{new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit' }).format(
									new Date(Date.parse(comment.date))
								)}
							</p>
						</div>
					);
				})}
				<CommentForm />
			</div>
		);
	}
	return <div />;
}

function CampsiteInfo(props) {
	if (props.campsite) {
		return (
			<div className="container">
				<div className="row">
					<div className="col">
						<Breadcrumb>
							<BreadcrumbItem>
								<Link to="/home">Directory</Link>
							</BreadcrumbItem>
							<BreadcrumbItem active>{props.campsite.name}</BreadcrumbItem>
						</Breadcrumb>
						<h2>{props.campsite.name}</h2>
						<hr />
					</div>
				</div>
				<div className="row">
					<RenderCampsite campsite={props.campsite} />
					<RenderComments comments={props.comments} />
				</div>
			</div>
		);
	}
	return <div />;
}

export default CampsiteInfo;
